       618 +              self.db_path, 
       619 +              Utc::now().timestamp()
       620 +          );
       621 +          fs::rename(&self.db_path, backup_path).await?;
       622 +          
       623 +          // Create new database
       624 +          self.initialize_schema().await?;
       625 +          
